#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="H:\Code\Learn\calorie-tracker\node_modules\.pnpm\ngrok@5.0.0-beta.2\node_modules\ngrok\bin\node_modules;H:\Code\Learn\calorie-tracker\node_modules\.pnpm\ngrok@5.0.0-beta.2\node_modules\ngrok\node_modules;H:\Code\Learn\calorie-tracker\node_modules\.pnpm\ngrok@5.0.0-beta.2\node_modules;H:\Code\Learn\calorie-tracker\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/h/Code/Learn/calorie-tracker/node_modules/.pnpm/ngrok@5.0.0-beta.2/node_modules/ngrok/bin/node_modules:/mnt/h/Code/Learn/calorie-tracker/node_modules/.pnpm/ngrok@5.0.0-beta.2/node_modules/ngrok/node_modules:/mnt/h/Code/Learn/calorie-tracker/node_modules/.pnpm/ngrok@5.0.0-beta.2/node_modules:/mnt/h/Code/Learn/calorie-tracker/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

# Support pipeline input
if ($MyInvocation.ExpectingInput) {
  $input | & "$basedir/../ngrok/bin/ngrok"   $args
} else {
  & "$basedir/../ngrok/bin/ngrok"   $args
}
$env:NODE_PATH=$env_node_path
exit $LASTEXITCODE
